import React, { useState, useEffect } from "react";
import Papa from "papaparse";
import "./App.css";

function App() {
  const [drinks, setDrinks] = useState([]);
  const [cart, setCart] = useState([]);

  useEffect(() => {
    Papa.parse("https://docs.google.com/spreadsheets/d/e/2PACX-1vQyZEDaYEI3m_--c6sPIH7cz9a4UtVK4eXFXFUrhRhDffm-UPQ6oOiC1pYnvPxdqvuOEoTsoVoDIh3X/pubhtml", {
      download: true,
      header: true,
      complete: (result) => {
        const parsed = result.data
          .filter(d => d.name && d.price)
          .map((item, index) => ({
            ...item,
            price: parseFloat(item.price),
            id: parseInt(item.id) || index,
          }));
        setDrinks(parsed);
      },
    });
  }, []);

  const addToCart = (drink) => {
    const existing = cart.find((item) => item.id === drink.id);
    if (existing) {
      const updated = cart.map((item) =>
        item.id === drink.id ? { ...item, quantity: item.quantity + 1 } : item
      );
      setCart(updated);
    } else {
      setCart([...cart, { ...drink, quantity: 1 }]);
    }
  };

  const removeFromCart = (id) => {
    setCart(cart.filter((item) => item.id !== id));
  };

  const updateQuantity = (id, amount) => {
    const updated = cart
      .map((item) =>
        item.id === id
          ? { ...item, quantity: Math.max(1, item.quantity + amount) }
          : item
      )
      .filter((item) => item.quantity > 0);
    setCart(updated);
  };

  const total = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);

  return (
    <div className="container">
      <h1>Blackout Bar</h1>
      {drinks.map((drink) => (
        <div key={drink.id} className="drink">
          <img src={drink.image} alt={drink.name} />
          <div className="drink-info">
            <h3>{drink.name}</h3>
            <p>{drink.description}</p>
            <p>¥{drink.price}</p>
            <button onClick={() => addToCart(drink)}>Add to Cart</button>
          </div>
        </div>
      ))}
      <div className="cart">
        <h2>Your Cart</h2>
        {cart.length === 0 ? (
          <p>No items in cart.</p>
        ) : (
          <>
            {cart.map((item) => (
              <div key={item.id} className="cart-item">
                <span>
                  {item.name} × {item.quantity}
                </span>
                <span>
                  ¥{item.price * item.quantity}{" "}
                  <button onClick={() => updateQuantity(item.id, 1)}>+</button>
                  <button onClick={() => updateQuantity(item.id, -1)}>-</button>
                  <button onClick={() => removeFromCart(item.id)}>x</button>
                </span>
              </div>
            ))}
            <h3>Total: ¥{total}</h3>
          </>
        )}
      </div>
    </div>
  );
}

export default App;
