import React, { useState } from 'react';
import './App.css';

const drinkList = [
  { id: 1, name: 'Red Bull 红牛', description: 'Energy Drink', price: 18 },
  { id: 2, name: 'Heineken 喜力', description: 'Beer', price: 15 },
  { id: 3, name: 'Long Island Iced Tea 长岛冰茶', description: 'Cocktail', price: 45 },
];

function App() {
  const [cart, setCart] = useState([]);

  const addToCart = (drink) => {
    setCart(prevCart => {
      const itemInCart = prevCart.find(item => item.id === drink.id);
      if (itemInCart) {
        return prevCart.map(item =>
          item.id === drink.id ? { ...item, quantity: item.quantity + 1 } : item
        );
      } else {
        return [...prevCart, { ...drink, quantity: 1 }];
      }
    });
  };

  const updateQuantity = (id, amount) => {
    setCart(prevCart =>
      prevCart.map(item =>
        item.id === id ? { ...item, quantity: Math.max(1, item.quantity + amount) } : item
      )
    );
  };

  const removeItem = (id) => {
    setCart(prevCart => prevCart.filter(item => item.id !== id));
  };

  const getTotal = () => {
    return cart.reduce((sum, item) => sum + item.price * item.quantity, 0);
  };

  return (
    <div className="App">
      <h1>Blackout Bar</h1>

      <section className="drinks">
        <h2>Browse Drinks</h2>
        {drinkList.map(drink => (
          <div key={drink.id} className="drink-card">
            <h3>{drink.name}</h3>
            <p>{drink.description}</p>
            <p>¥{drink.price}</p>
            <button onClick={() => addToCart(drink)}>Add to Cart</button>
          </div>
        ))}
      </section>

      <section className="cart">
        <h2>Shopping Cart</h2>
        {cart.length === 0 ? (
          <p>Your cart is empty</p>
        ) : (
          <ul>
            {cart.map(item => (
              <li key={item.id}>
                {item.name} - ¥{item.price} × {item.quantity} = ¥{item.price * item.quantity}
                <div>
                  <button onClick={() => updateQuantity(item.id, -1)}>-</button>
                  <button onClick={() => updateQuantity(item.id, 1)}>+</button>
                  <button onClick={() => removeItem(item.id)}>Remove</button>
                </div>
              </li>
            ))}
          </ul>
        )}
        <h3>Total: ¥{getTotal()}</h3>
        {cart.length > 0 && <button onClick={() => alert('Proceeding to payment...')}>Proceed to Payment</button>}
      </section>
    </div>
  );
}

export default App;
